package chapter3;

public class qiantao {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       
		for (int i=1;i<10;i++)
		{
			for(int j=1;j<=i;j++)
			{
				System.out.print(i+"*"+j+"="+i*j+"\t");

			}
			//System.out.println();
			System.out.print("\n");
		}
	}

}
